SELECT

FROM "GFTW"."TS_INVERTER_REALTIMEDATA" T1
WHERE T1."ID" IN (
    SELECT "MAX"(T2."ID")
    FROM "GFTW"."TS_INVERTER_REALTIMEDATA" t2
    WHERE "TO_CHAR"(T2."CTIME",'YYYYMMDD') = #{toDayDate}
      AND T2."INVERTER_ID" IN

         #{item}

     GROUP BY t2."INVERTER_ID"
  )

